export declare enum NotificationType {
    mentioned = 1,
    replied = 2,
    quoted = 3,
    edited = 4,
    liked = 5,
    privateMessage = 6,
    invitedToPrivateMessage = 7,
    inviteeAccepted = 8,
    posted = 9,
    movedPost = 10,
    linked = 11,
    grantedBadge = 12,
    invitedToTopic = 13,
    custom = 14,
    groupMentioned = 15,
    groupMessageSummary = 16,
    watchingFirstPost = 17,
    topicReminder = 18,
    likedConsolidated = 19,
    postApproved = 20,
    codeReviewCommitApproved = 21,
    membershipRequestAccepted = 22,
    membershipRequestConsolidated = 23,
    bookmarkReminder = 24,
    reaction = 25,
    votesReleased = 26,
    eventReminder = 27,
    eventInvitation = 28
}
export interface GroupNotification {
    id: number;
    userId: number;
    notificationType: NotificationType.groupMentioned | NotificationType.groupMessageSummary;
    read: boolean;
    createdAt: string;
    postNumber: string | null;
    topicId: string | null;
    slug: string | null;
    data: {
        groupId: number;
        groupName: string;
        inboxCount: number;
        username: string;
    };
}
export interface TopicNotification {
    id: number;
    notificationType: NotificationType.privateMessage | NotificationType.posted | NotificationType.replied | NotificationType.liked;
    read: boolean;
    createdAt: string;
    postNumber: number;
    topicId: number;
    fancyTitle: string;
    slug: string;
    data: {
        topicTitle: string;
        originalPostId: number;
        originalPostType: number;
        originalUsername: string;
        revisionNumber: number | string;
        displayUsername: string;
    };
}
export interface UnknownNotification {
    id: number;
    notificationType: NotificationType.watchingFirstPost | NotificationType.mentioned;
}
export interface NotificationList {
    notifications: Array<TopicNotification | GroupNotification>;
    seenNotificationId: number;
    totalRowsNotifications?: number;
    loadMoreNotifications?: string;
}
export interface MarkNotificationRead {
    id: number;
}
